}
.tie--center {
  top: 3.6rem;
  left: 2.1rem;
  border-radius: 100%;
  width: 14px;
  height: 14px;
}
.tie--left {
  position: absolute;
  top: 4rem;
  left: 2.375rem;
  background: #8E2933;
}
.tie--left-1 {
  top: -0.625rem;
  left: 0.625rem;
  border-radius: 30%;
  transform: rotate(-8deg);
  width: 24px;
  height: 18px;
}
.tie--left-2 {
  left: 0.625rem;
  border-radius: 30%;
  transform: rotate(10deg);
  width: 24px;
  height: 12px;
}
.tie--right {
  position: absolute;
  top: 4rem;
  background: #8E2933;
}
.tie--right-1 {
  top: -0.625rem;
  left: 0.625rem;
  border-radius: 30%;
  transform: rotate(10deg);
  width: 24px;
  height: 18px;
}
.tie--right-2 {
  left: 0.625rem;
  border-radius: 30%;
  transform: rotate(-10deg);
  width: 24px;
  height: 12px;
}

.blink {
  background-color: #49B90D;
  border-radius: 50%;
  box-sizing: content-box;
  height: 0.375rem;
  vertical-align: middle;
  width: 0.375rem;
}
.blink.is-pulse:before {
  animation: blinkAnimation 1.5s infinite;
  background-color: #49B90D;
  border-radius: 50%;
  bottom: 0;
  content: "";
  height: 0.375rem;
  left: 0;
  position: absolute;
  width: 0.375rem;
}
.blink.is-bordered {
  height: 0.5rem;
  width: 0.5rem;
  border: 2px solid #49B90D;
}
.blink.is-bordered:before {
  height: 0.5rem;
  width: 0.5rem;
}
.blink.is-success {
  background-color: #49B90D;
}
.blink.is-success:before {
  background-color: #49B90D;
}

@keyframes blinkAnimation {
  0% {
    opacity: 1;
    transform: scale(0);
  }
  70% {
    transform: scale(2.5);
  }
  100% {
    opacity: 0;
    transform: scale(0);
  }
}
:root {
  --hue: 223;
  --bg: hsl(var(--hue),10%,90%);
  --fg: hsl(var(--hue),10%,10%);
  --primary: hsl(var(--hue),90%,50%);
  --trans-dur: 0.3s;
  --trans-timing: cubic-bezier(0.65,0,0.35,1);
}

.link {
  color: var(--primary);
  transition: color var(--trans-dur);
}

.heading-primary {
  font-size: 2em;
  margin: 0 0 3rem;
  padding-top: 1.5rem;
  text-align: center;
}

.btn {
  background-color: var(--fg);
  margin: 0.625rem;
  font-size: 1rem;
  border-radius: 0.25em;
  border: none;
  color: var(--bg);
  cursor: pointer;
  padding: 0.375em 0.75em;
  transition: background-color calc(var(--trans-dur) / 2) linear, color var(--trans-dur);
  -webkit-tap-highlight-color: transparent;
