@mixin mediaquery($breakpoint) {
    @if $breakpoint == xs {
        @media (min-width: 36rem) {
        @content;
        }
    }

    @if $breakpoint == sm {
        @media (min-width: 48rem) {
        @content;
        }
    }

    @else if $breakpoint == md {
        @media (min-width: 64rem) {
        @content;
        }
    }

    @else if $breakpoint == lg {
        @media (min-width: 75rem) {
        @content;
        }
    }

    @else {
        @content;
    }
}

