			background-color: hsl(var(--hue),10%,50%,0.4);
		}

		&:hover {
			background-color: hsl(var(--hue),10%,50%,0.4);
		}

		&-icon {
			display: block;
			pointer-events: none;
			transition: transform var(--trans-dur) var(--trans-timing);
		}
	}

	&__meta {
		width: 100%;
	}

	&__date {
		font-size: 0.833em;
		line-height: 2.4;
	}

	&__title {
		font-size: 1.5em;
		line-height: 1.333;
	}

	&__item-body {
		border-radius: 0.375em;
		overflow: hidden;
		margin-top: 0.5em;
		height: 0;
	}

	&__item-body-content {
		background-color: hsl(var(--hue),10%,50%,0.2);
		opacity: 0;
		padding: 1rem;
		visibility: hidden;
		transition:
			opacity var(--trans-dur) var(--trans-timing),
			visibility var(--trans-dur) steps(1,end);
	}

	&__item-body--expanded {
		height: auto;
	}
}

[dir="rtl"] .c-qualification__arrow-icon {
	transform: rotate(-90deg);
}

[dir="rtl"] .c-qualification__item:not(:last-child):before {
	right: 2.625em;
	left: auto;
	transform: translateX(50%);
}

/* Expanded state */

.c-qualification__item-body--expanded .c-qualification__item-body-content {
	opacity: 1;
	visibility: visible;
	transition-delay: var(--trans-dur), 0s;
}

.c-qualification__arrow[aria-expanded="true"] .c-qualification__arrow-icon {
	transform: rotate(-90deg);
}

/* Dark theme */
@media (prefers-color-scheme: dark) {
	:root {
		--bg: hsl(var(--hue),10%,10%);
		--fg: hsl(var(--hue),10%,90%);
		--primary: hsl(var(--hue),90%,70%);
	}
}

