.contacts-text {
    font-size: 30px;
    background: -webkit-linear-gradient(#D1C4E9, #5b51ce);   
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    text-align: center;
    color: #fff;
}

.contacts-paragh {
    text-align: center;
    margin-top: 10px;
    margin-bottom: 20px;
}

.nav-contacts {
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    text-align: center;
}

.contact {
    display: flex;
    flex-wrap: wrap;
    padding: 0;
    justify-content: center;
}

.contact li {
    position: relative;
    width: 60px;
    height: 60px;
    border-radius: 60px;
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1;
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
    transition: 0.5s;
}

.contact li:hover {
    width: 180px;
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0);
}

.contact li::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 50px;
    background: linear-gradient(45deg, var(--i), var(--j));
    opacity: 0;
    transition: 0.5s;
}

.contact li:hover::before {
    opacity: 1;
    z-index: -1;
}

.contact li::after {
    content: '';
    position: absolute;
    top: 10px;
    width: 100%;
    height: 100%;
    border-radius: 60px;
    background: linear-gradient(45deg, var(--i), var(--j));
    opacity: 0;
    transition: 0.5s;
    filter: blur(15px);
}

.contact li:hover::after {
    opacity: 0.5;
}

.contact li .c-contact--icon {
    color: #fff;
    font-size: 1.75rem !important;
    transition: 0.5s;
    transition-delay: 0.25s;
}

.contact li:hover .c-contact--icon {
    transform: scale(0);
    font-size: 1.75rem;
    transition-delay: 0.25s;
}

.contact li span {
    position: absolute;
}

.contact li .titulo {
    color: #fff;
    font-size: 1.1rem !important;
    text-transform: uppercase;
    letter-spacing: 0.1rem;
    transform: scale(0);
    transition: 0.5s;
    transition-delay: 0s;
}

.contact li:hover .titulo {
    transform: scale(1);
    transition-delay: 0.25s;
}

.title-purple {
    color: pink;
    margin-bottom: 30px;
    font-weight: 800;
}
