const carousel = document.querySelector('.carousel');
const items = document.querySelectorAll('.item');
const leftButton = document.querySelector('.button--left');
const rightButton = document.querySelector('.button--right');

leftButton.addEventListener('click', function() {
    roll('left');
});
rightButton.addEventListener('click', function() {
    roll('right');
});

function roll(direction) {
    carousel.classList.add(`moving-${direction}`);

    items.forEach((item) => {
        const startPosition = parseInt(item.dataset.position, 10);
        let endPosition;

        if (direction === 'right') {
            endPosition = startPosition + 1;
        } else if (direction === 'left') {
            endPosition = startPosition - 1;
        }

        if (endPosition > items.length) {
            endPosition = 1;
            item.style.zIndex = '-1';
        } else if (endPosition < 1) {
            endPosition = items.length;
            item.style.zIndex = '-1';
        } else {
            item.style.zIndex = '';
        }
        item.dataset.position = endPosition;
    });

    items.forEach((item) => {
        item.addEventListener('transitionend', function handleTransitionEnd() {
            item.removeEventListener('transitionend', handleTransitionEnd);
            carousel.classList.remove(`moving-${direction}`);
        });
    });

    const activeItem = document.querySelector('[data-position="3"]');
    if (activeItem) {
        console.log(activeItem);
    } else {
        console.error('No item found with data-position="3"');
    }
}


